function toggleMobileNav(){document.querySelector("header").classList.toggle("header--is-opened"),document.querySelector(".mobile-nav").classList.toggle("mobile-nav--is-opened")}function hideHeader(){var navbar=document.querySelector("nav"),sticky=navbar.offsetTop;window.pageYOffset>=sticky?navbar.classList.add("sticky"):navbar.classList.remove("sticky")}document.querySelectorAll(".header__trigger").forEach(item=>item.addEventListener("click",toggleMobileNav)),window.addEventListener("scroll",hideHeader);