function toggleMobileNav(){document.querySelector("header").classList.toggle("header--is-opened"),document.querySelector(".mobile-nav").classList.toggle("mobile-nav--is-opened")}document.querySelectorAll(".header__trigger").forEach(item=>item.addEventListener("click",toggleMobileNav));var navbar=document.querySelector("nav"),sticky=navbar.offsetTop;function hideHeader(){window.pageYOffset>=sticky?navbar.classList.add("nav--sticky"):navbar.classList.remove("nav--sticky")}window.onscroll=function(){this.hideHeader()};